import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as l,b as e}from"./app.b48e0c05.js";const r={},h=e('<h2 id="_1-1-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u57FA\u672C\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#_1-1-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a> 1.1 \u64CD\u4F5C\u7CFB\u7EDF\u7684\u57FA\u672C\u6982\u5FF5</h2><p>\u7CFB\u7EDF\u8F6F\u4EF6\uFF0C\u63A7\u5236\u8BA1\u7B97\u673A\u786C\u4EF6\u548C\u8F6F\u4EF6\u8D44\u6E90</p><p>\u64CD\u4F5C\u7CFB\u7EDF\u7684\u7279\u5F81\uFF1A\u6700\u4E3B\u8981\u7684\u662F\u5E76\u53D1\u548C\u5171\u4EAB</p><p>\uFF081\uFF09\u5E76\u53D1\uFF1A\u5206\u65F6\u4EA4\u66FF\u3002\u5355\u5904\u7406\u673A\u540C\u65F6\u53EA\u80FD\u8FD0\u884C\u4E00\u4E2A\u8FDB\u7A0B</p><p>\uFF082\uFF09\u5171\u4EAB\uFF1A\u7CFB\u7EDF\u8D44\u6E90\u53EF\u4F9B\u591A\u4E2A\u5E76\u53D1\u7684\u8FDB\u7A0B\u540C\u65F6\u4F7F\u7528\uFF0C\u540C\u6B65\u4E92\u65A5</p><p>\uFF083\uFF09\u865A\u62DF\uFF1A\u7269\u7406\u5B9E\u4F53-&gt;\u903B\u8F91\u4E8B\u7269</p><p>\uFF084\uFF09\u5F02\u6B65\uFF1A\u591A\u4E2A\u7A0B\u5E8F\u6267\u884C\u901F\u5EA6\u4E0D\u53EF\u9884\u77E5\uFF0C\u4F46\u7ED3\u679C\u4E00\u5B9A</p><p>\u64CD\u4F5C\u7CFB\u7EDF\u7684\u529F\u80FD\uFF1A\u5904\u7406\u673A\u7BA1\u7406\uFF0C\u5B58\u50A8\u5668\u7BA1\u7406\uFF0C\u6587\u4EF6\u7BA1\u7406\uFF0C\u8BBE\u5907\u7BA1\u7406</p><p>\u64CD\u4F5C\u7CFB\u7EDF\u7684\u63A5\u53E3\uFF1A\u547D\u4EE4\u63A5\u53E3\uFF08\u8054\u673A\uFF1A\u4EA4\u4E92\u5F0F\uFF0C\u8131\u673A\uFF1A\u6279\u5904\u7406\uFF09\uFF0C\u7CFB\u7EDF\u8C03\u7528\uFF08\u7A0B\u5E8F\u63A5\u53E3\uFF0C\u7ED9\u7F16\u7A0B\u4EBA\u5458\u7684\u63A5\u53E3\uFF09</p><p>\u5E93\u51FD\u6570\u6765\u4F7F\u7528\u7CFB\u7EDF\u8C03\u7528</p><h2 id="_1-2-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u53D1\u5C55\u548C\u5206\u7C7B" tabindex="-1"><a class="header-anchor" href="#_1-2-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u53D1\u5C55\u548C\u5206\u7C7B" aria-hidden="true">#</a> 1.2 \u64CD\u4F5C\u7CFB\u7EDF\u7684\u53D1\u5C55\u548C\u5206\u7C7B</h2><p>\u53D1\u5C55\u6D41\u7A0B\uFF1A\u624B\u5DE5\u64CD\u4F5C\u9636\u6BB5-&gt;\u6279\u5904\u7406\u9636\u6BB5-&gt;\u5206\u65F6\u7CFB\u7EDF-&gt;\u5B9E\u65F6\u7CFB\u7EDF-&gt;PC\u64CD\u4F5C\u7CFB\u7EDF</p><p>\u6279\u5904\u7406\u7CFB\u7EDF\uFF1A<strong>\u5C06\u4E00\u6279\u4F5C\u4E1A\u8FDB\u5165\u5185\u5B58</strong>\uFF0C\u4EA4\u7ED9\u64CD\u4F5C\u7CFB\u7EDF\u5904\u7406\uFF0C\u7528\u6237\u8131\u673A\u3002\u4F18\u70B9\uFF1A\u8D44\u6E90\u5229\u7528\u7387\u9AD8\uFF0C<strong>\u7F3A\u70B9\uFF1A\u65E0\u4EA4\u4E92\u80FD\u529B</strong></p><ul><li><p>\u5355\u9053\u6279\u5904\u7406\u7CFB\u7EDF\uFF1A\u5185\u5B58\u4E2D\u59CB\u7EC8\u4FDD\u6301\u4E00\u9053\u4F5C\u4E1A\u3002\u5982\u679C\u7A0B\u5E8F\u8FD0\u884C\u65F6\u53D1\u51FAio\u8BF7\u6C42\uFF0C\u90A3CPU\u5C31\u8981\u7B49\u5F85\u4F4E\u901F\u7684io</p></li><li><p>\u591A\u9053\u6279\u5904\u7406\u7CFB\u7EDF\uFF1A\u5141\u8BB8\u591A\u4E2A\u7A0B\u5E8F\u540C\u65F6\u8FDB\u5165\u5185\u5B58\uFF0C\u5728CPU\u4E2D\u4EA4\u66FF\u8FD0\u884C\u3002\u4E00\u4E2A\u7A0B\u5E8Fio\u7684\u540C\u65F6\uFF0CCPU\u53EF\u4EE5\u8F6C\u800C\u6267\u884C\u5176\u4ED6\u7A0B\u5E8F\u3002\u4F46\u662F\u7528\u6237\u4E0D\u77E5\u9053\u7A0B\u5E8F\u7684\u8FD0\u884C\u60C5\u51B5\uFF0C\u4E0D\u80FD\u548C\u8BA1\u7B97\u673A\u4EA4\u4E92\uFF0C\u53EA\u80FD\u901A\u8FC7\u63D0\u524D\u5199\u597D\u7684\u7528\u6237\u8BF4\u660E\u4E66\u6765\u8C03\u8BD5</p></li></ul><p>\u5206\u65F6\u64CD\u4F5C\u7CFB\u7EDF\uFF1A\u5904\u7406\u5668\u7684\u8FD0\u884C\u65F6\u95F4\u5206\u6210\u5F88\u77ED\u7684\u65F6\u95F4\u7247\uFF0C\u8F6E\u6D41\u5206\u914D\u7ED9\u5404\u4F5C\u4E1A\u3002\u591A\u4E2A\u7528\u6237\u5171\u4EAB\u4E00\u53F0\u4E3B\u673A\uFF0C\u4EA4\u4E92\u4E92\u4E0D\u5E72\u6270</p><p>\u5B9E\u65F6\u64CD\u4F5C\u7CFB\u7EDF\uFF1A\u8BA2\u7968\u3001\u94F6\u884C\uFF0C\u4E25\u683C\u53CA\u65F6</p><h2 id="_1-3-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u8FD0\u884C\u73AF\u5883" tabindex="-1"><a class="header-anchor" href="#_1-3-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u8FD0\u884C\u73AF\u5883" aria-hidden="true">#</a> 1.3 \u64CD\u4F5C\u7CFB\u7EDF\u7684\u8FD0\u884C\u73AF\u5883</h2><h3 id="_1-\u64CD\u4F5C\u7CFB\u7EDF\u8FD0\u884C\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#_1-\u64CD\u4F5C\u7CFB\u7EDF\u8FD0\u884C\u673A\u5236" aria-hidden="true">#</a> 1.\u64CD\u4F5C\u7CFB\u7EDF\u8FD0\u884C\u673A\u5236</h3><p>CPU\u6709\u4E24\u79CD\u72B6\u6001\uFF1A\u7528\u6237\u6001\u65F6\uFF0C\u53EA\u80FD\u6267\u884C\u975E\u7279\u6743\u6307\u4EE4\uFF0C\u5185\u6838\u6001\u53EF\u4EE5\u6267\u884C\u7279\u6743\u6307\u4EE4</p><p>\u64CD\u4F5C\u7CFB\u7EDF\u5185\u6838\u5305\u62EC\uFF1A</p><ul><li>\u65F6\u949F\u7BA1\u7406\uFF1A\u8BA1\u65F6\uFF0C\u65F6\u95F4\u7247\u8F6E\u8F6C\u8C03\u5EA6\u5207\u6362\u8FDB\u7A0B</li><li>\u4E2D\u65AD\u673A\u5236</li><li>\u539F\u8BED\uFF1A\u4E0D\u53EF\u88AB\u4E2D\u65AD\u7684\u539F\u5B50\u8FDB\u7A0B</li><li>\u7CFB\u7EDF\u63A7\u5236\u7684\u6570\u636E\u7ED3\u6784\uFF08\u8FDB\u7A0B\u7BA1\u7406\uFF0C\u5B58\u50A8\u5668\u7BA1\u7406\uFF0C\u8BBE\u5907\u7BA1\u7406\uFF09\uFF0C\u4E0E\u786C\u4EF6\u5173\u7CFB\u4E0D\u7D27\u5BC6</li></ul><p>\u7528\u6237\u6001\u5230\u5185\u6838\u6001\u5207\u6362\u9700\u8981\u901A\u8FC7\u4E2D\u65AD\u6216\u5F02\u5E38\uFF0C\u53CD\u8FC7\u6765\u5219\u9700\u8981\u4E2D\u65AD\u8FD4\u56DE\u6307\u4EE4\u3002</p><p>\u8FDB\u7A0B\u4ECE\u7528\u6237\u6001-&gt;\u5185\u6838\u6001\u9700\u8981\u8BBF\u7BA1\u6307\u4EE4\uFF0C\u4E2D\u65AD\u5904\u7406\u7A0B\u5E8F\u5728\u5185\u6838\u6001\u8FD0\u884C\u3002</p><h3 id="_2-\u4E2D\u65AD\u548C\u5F02\u5E38" tabindex="-1"><a class="header-anchor" href="#_2-\u4E2D\u65AD\u548C\u5F02\u5E38" aria-hidden="true">#</a> 2.\u4E2D\u65AD\u548C\u5F02\u5E38</h3><p>\u786C\u4EF6\u4E2D\u65AD\uFF1A\u4E2D\u65AD\u4FE1\u53F7\u6765\u81EA\u786C\u4EF6\u8BBE\u5907</p><ul><li>\u5916\u4E2D\u65AD\uFF08\u4E2D\u65AD\uFF09\uFF1A\u6765\u81EA\u5916\u90E8\u8BBE\u5907\uFF0C\u5305\u62ECio\u8BBE\u5907\u4E2D\u65AD\u3001\u5916\u90E8\u4FE1\u53F7\u4E2D\u65AD\u3001\u5B9A\u65F6\u5668\u5F15\u8D77\u7684\u65F6\u949F\u4E2D\u65AD</li><li>\u5185\u4E2D\u65AD\uFF08\u5F02\u5E38\uFF09\uFF1A\u4E2D\u65AD\u4FE1\u53F7\u6765\u81EACPU\u5185\u90E8\u6307\u4EE4\u6267\u884C <ul><li>\u6545\u969Cfault\uFF1A\u53EF\u6062\u590D\u7684\u9519\u8BEF\uFF0C\u5982\u7F3A\u9875\uFF08\u901A\u8FC7\u8BBF\u5B58\u6307\u4EE4\u4EA7\u751F\u7684\u5F02\u5E38\uFF09</li><li>\u7EC8\u6B62abort\uFF1A\u4E0D\u53EF\u6062\u590D\u7684\u9519\u8BEF\uFF0C\u5982\u975E\u6CD5\u6307\u4EE4\uFF0C\u9664\u6570\u4E3A0</li></ul></li></ul><p>\u8F6F\u4E2D\u65AD\uFF1A\u901A\u8FC7\u5F53\u524D\u8FDB\u7A0B\u4E2D\u65AD\u6307\u4EE4int\u4EA7\u751F\u7684\u4E2D\u65AD\uFF0C\u5176\u5B9E\u662F\u7A0B\u5E8F\u7684\u6B63\u5E38\u6267\u884C</p><ul><li>\u9677\u5165trap\uFF1A\u6709\u610F\u7684\u5F02\u5E38\uFF0C\u5982\u7CFB\u7EDF\u8C03\u7528\uFF08\u7528\u6237\u6001\u5230\u5185\u6838\u6001\u7684\u5207\u6362\uFF09</li></ul><p>CPU\u54CD\u5E94\u4E2D\u65AD\u540E\uFF0C\u4E2D\u65AD\u5904\u7406\u8FC7\u7A0B\uFF1A</p><ul><li><strong>\u4E2D\u65AD\u9690\u6307\u4EE4</strong>\uFF08\u786C\u4EF6\u76F4\u63A5\u5B9E\u73B0\uFF09\u505A\u7684\u4E8B\uFF1A\uFF08\u7528\u6237\u6001\uFF09 <ul><li>\u5173\u4E2D\u65AD\uFF1A\u56E0\u4E3A\u4FDD\u62A4\u4E2D\u65AD\u73B0\u573A\u65F6\u4E0D\u80FD\u88AB\u4E2D\u65AD\u6253\u6270</li><li>\u4FDD\u5B58\u65AD\u70B9\uFF1A\u5C06\u5F53\u524DPC\u503C\u538B\u6808</li><li>\u4E2D\u65AD\u670D\u52A1\u7A0B\u5E8F\u5BFB\u5740\uFF1A\u5C06\u4E2D\u65AD\u5411\u91CF\uFF08\u4E2D\u65AD\u670D\u52A1\u7A0B\u5E8F\u7684\u5165\u53E3\u5730\u5740\uFF09\u653E\u5165PC</li></ul></li><li><strong>\u4E2D\u65AD\u670D\u52A1\u7A0B\u5E8F</strong>\u505A\u7684\u4E8B\uFF1A\uFF08\u5185\u6838\u6001\uFF09 <ul><li>\u4FDD\u5B58\u73B0\u573A\u548C<strong>\u5C4F\u853D\u5B57</strong>\uFF1A\u901A\u7528\u5BC4\u5B58\u5668\u7684\u503C</li><li>\u5F00\u4E2D\u65AD\uFF1A\u4E3A\u4E86\u4E2D\u65AD\u5D4C\u5957\uFF0C\u63A5\u53D7\u65B0\u7684\u4E2D\u65AD</li><li>\u6267\u884C\u4E2D\u65AD\u670D\u52A1\u7A0B\u5E8F</li><li>\u5173\u4E2D\u65AD\uFF1A\u4FDD\u8BC1\u6062\u590D\u73B0\u573A\u65F6\u4E0D\u80FD\u88AB\u6253\u6270</li><li>\u6062\u590D\u73B0\u573A\u548C\u5C4F\u853D\u5B57</li><li>\u5F00\u4E2D\u65AD</li><li>\u4E2D\u65AD\u8FD4\u56DE</li></ul></li></ul><h3 id="_3-\u7CFB\u7EDF\u8C03\u7528" tabindex="-1"><a class="header-anchor" href="#_3-\u7CFB\u7EDF\u8C03\u7528" aria-hidden="true">#</a> 3.\u7CFB\u7EDF\u8C03\u7528</h3><p>\u7A0B\u5E8F\u8C03\u7528\u64CD\u4F5C\u7CFB\u7EDF\u63D0\u4F9B\u7684\u529F\u80FD\u5305\u62EC\u8BBE\u5907\u7BA1\u7406\u3001\u6587\u4EF6\u7BA1\u7406\u3001\u8FDB\u7A0B\u63A7\u5236\u3001\u8FDB\u7A0B\u901A\u4FE1\u3001\u5185\u5B58\u7BA1\u7406\u3002</p><p>\u7528\u6237\u7A0B\u5E8F\u901A\u8FC7<code>int 0x80</code>\u547D\u4EE4\u4EA7\u751F\u8F6F\u4E2D\u65AD\uFF0C\u7136\u540E\u901A\u8FC7<strong>trap\u9677\u5165\u6307\u4EE4</strong>\u53D1\u8D77\u7CFB\u7EDF\u8C03\u7528\uFF0C\u628ACPU\u4F7F\u7528\u6743\u4E3B\u52A8\u4EA4\u7ED9\u5185\u6838\uFF0C\u5373\u7528\u6237\u6001\u5230\u5185\u6838\u6001\u3002\u6B64\u65F6\uFF0C\u5BC4\u5B58\u5668eax\u4E2D\u4F1A\u5B58\u653E\u7CFB\u7EDF\u8C03\u7528\u53F7\uFF0C\u6839\u636E\u7CFB\u7EDF\u8C03\u7528\u53F7\u67E5\u627E\u7CFB\u7EDF\u8C03\u7528\u8868sys_call_table\u4E2D\u5BF9\u5E94\u7684\u7CFB\u7EDF\u8C03\u7528\u670D\u52A1\u7A0B\u5E8F\u5165\u53E3\u5730\u5740\u3002\u5185\u6838\u5904\u7406\u5B8C\u540E\uFF0C\u518D\u628ACPU\u4F7F\u7528\u6743\u8FD8\u7ED9\u7528\u6237\u7A0B\u5E8F</p><h2 id="_1-4-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u4F53\u7CFB\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#_1-4-\u64CD\u4F5C\u7CFB\u7EDF\u7684\u4F53\u7CFB\u7ED3\u6784" aria-hidden="true">#</a> 1.4 \u64CD\u4F5C\u7CFB\u7EDF\u7684\u4F53\u7CFB\u7ED3\u6784</h2><h3 id="\u5206\u5C42\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5206\u5C42\u6CD5" aria-hidden="true">#</a> \u5206\u5C42\u6CD5</h3><p>\u5C06\u64CD\u4F5C\u7CFB\u7EDF\u5206\u6210\u82E5\u5E72\u5C42\u6B21\uFF0C0\u4E3A\u786C\u4EF6\uFF08\u6700\u5E95\u5C42\uFF09\uFF0Cn\u4E3A\u7528\u6237\u63A5\u53E3</p><p>\u6BCF\u5C42\u53EA\u80FD\u8C03\u7528\u76F8\u90BB\u7684\u66F4\u4F4E\u5C42\u7684\u670D\u52A1</p><h3 id="\u6A21\u5757\u5316" tabindex="-1"><a class="header-anchor" href="#\u6A21\u5757\u5316" aria-hidden="true">#</a> \u6A21\u5757\u5316</h3><p>\u5C06\u64CD\u4F5C\u7CFB\u7EDF\u6309\u529F\u80FD\u5212\u5206\u4E3A\u591A\u4E2A\u6A21\u5757\uFF0C\u5404\u6A21\u5757\u4E4B\u95F4\u901A\u8FC7\u63A5\u53E3\u901A\u4FE1</p><h3 id="\u5927\u5185\u6838\u4E0E\u5FAE\u5185\u6838" tabindex="-1"><a class="header-anchor" href="#\u5927\u5185\u6838\u4E0E\u5FAE\u5185\u6838" aria-hidden="true">#</a> \u5927\u5185\u6838\u4E0E\u5FAE\u5185\u6838</h3><p>\u5927\u5185\u6838\uFF1A\u64CD\u4F5C\u7CFB\u7EDF\u4E3B\u8981\u6A21\u5757\u90FD\u4F5C\u4E3A\u6574\u4F53\u8FD0\u884C\u5728\u5185\u6838\u6001\u3002</p><ul><li>\u6027\u80FD\u9AD8\uFF0C\u4F46\u662F\u589E\u52A0\u7CFB\u7EDF\u670D\u52A1\u65F6\u9700\u8981\u4FEE\u6539\u5185\u6838\uFF0C\u63A5\u53E3\u590D\u6742</li></ul><p>\u5FAE\u5185\u6838\uFF1A\u5185\u6838\u6001\u53EA\u6709\u6700\u57FA\u672C\u7684\u529F\u80FD\uFF08\u8FDB\u7A0B\u7BA1\u7406\u7B49\uFF09</p><ul><li>\u5FAE\u5185\u6838\u57FA\u4E8E\u7528\u6237/\u670D\u52A1\u5668\u6A21\u5F0F\uFF0C\u53EA\u6709\u5FAE\u5185\u6838\u8FD0\u884C\u5728\u5185\u6838\u6001\uFF0C\u5176\u4ED6\u6A21\u5757\u5728\u7528\u6237\u6001</li><li>\u53EF\u9760\u6027\u9AD8\uFF0C\u4F46\u9700\u8981\u9891\u7E41\u5207\u6362\uFF0C\u6027\u80FD\u5DEE</li></ul><h2 id="_1-5-\u7A0B\u5E8F\u8FD0\u884C\u73AF\u5883" tabindex="-1"><a class="header-anchor" href="#_1-5-\u7A0B\u5E8F\u8FD0\u884C\u73AF\u5883" aria-hidden="true">#</a> 1.5 \u7A0B\u5E8F\u8FD0\u884C\u73AF\u5883</h2><h3 id="\u8FDB\u7A0B\u7684\u5185\u5B58\u6620\u50CF\u4E0E\u5730\u5740\u7A7A\u95F4" tabindex="-1"><a class="header-anchor" href="#\u8FDB\u7A0B\u7684\u5185\u5B58\u6620\u50CF\u4E0E\u5730\u5740\u7A7A\u95F4" aria-hidden="true">#</a> \u8FDB\u7A0B\u7684\u5185\u5B58\u6620\u50CF\u4E0E\u5730\u5740\u7A7A\u95F4</h3><p>\u4E00\u4E2A\u7A0B\u5E8F\u4ECE\u786C\u76D8\u8C03\u5165\u5185\u5B58\u5C31\u6784\u6210\u4E86\u8FDB\u7A0B\u7684\u5185\u5B58\u6620\u50CF</p><p>\u5185\u5B58\u6620\u50CF\u4ECE\u4E0A\u5230\u4E0B\uFF08\u9AD8\u5730\u5740\u5230\u4F4E\u5730\u5740\uFF09\uFF1A</p><ul><li>\u6808\uFF1A\u7528\u6765\u5B9E\u73B0\u54C8\u51FD\u6570\u8C03\u7528</li><li>\u5806\uFF1A\u5B58\u653E\u52A8\u6001\u5206\u914D\u7684\u53D8\u91CF</li><li>\u6570\u636E\u6BB5\uFF1A\u5168\u5C40\u53D8\u91CF\u548C\u9759\u6001\u53D8\u91CF</li><li>\u4EE3\u7801\u6BB5\uFF1A\u7A0B\u5E8F\u7684\u4E8C\u8FDB\u5236\u4EE3\u7801</li></ul><p>\u7A0B\u5E8F\u8C03\u5165\u5185\u5B58\u540E\uFF0C\u6570\u636E\u6BB5\u548C\u4EE3\u7801\u6BB5\u5927\u5C0F\u56FA\u5B9A</p><p>\u6B64\u5916\uFF0C\u8FD8\u6709\u8FDB\u7A0B\u63A7\u5236\u5757PCB\u5B58\u653E\u5728\u7CFB\u7EDF\u533A</p><h2 id="_1-6-\u64CD\u4F5C\u7CFB\u7EDF\u5F15\u5BFC\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#_1-6-\u64CD\u4F5C\u7CFB\u7EDF\u5F15\u5BFC\u8FC7\u7A0B" aria-hidden="true">#</a> 1.6 \u64CD\u4F5C\u7CFB\u7EDF\u5F15\u5BFC\u8FC7\u7A0B</h2><ul><li>CPU\u6267\u884CROM\u4E2D\u7684boot\u7A0B\u5E8F\uFF0C\u5C06BIOS\u7A0B\u5E8F\uFF08\u57FA\u672C\u8F93\u5165\u8F93\u51FA\u7CFB\u7EDF\uFF09\u7684\u7B2C\u4E00\u6761\u6307\u4EE4\u653E\u5165\u6307\u4EE4\u5BC4\u5B58\u5668\uFF0C\u5F00\u59CB\u6267\u884CBIOS</li><li>BIOS\u786C\u4EF6\u81EA\u68C0\uFF0C\u901A\u8FC7\u904D\u5386\u65B9\u5F0F\u5BFB\u627E\u5E26\u6709\u4E3B\u5F15\u5BFC\u8BB0\u5F55\u7684\u7CFB\u7EDF\u786C\u76D8</li><li>\u52A0\u8F7D\u4E3B\u5F15\u5BFC\u8BB0\u5F55MBR\uFF0C\u6839\u636E\u6807\u8BC6\u7B26\u627E\u5230\u5F15\u5BFC\u786C\u76D8</li><li>\u52A0\u8F7D\u786C\u76D8\u5206\u533A\u8868\uFF0CMBR\u627E\u5230\u54EA\u4E2A\u786C\u76D8\u5206\u533A\u542B\u6709\u64CD\u4F5C\u7CFB\u7EDF</li><li>\uFF08\u52A0\u8F7D\u786C\u76D8\u6D3B\u52A8\u5206\u533A\uFF0C\u5206\u533A\u5F15\u5BFC\u8BB0\u5F55PBR\uFF0C\u542F\u52A8\u7BA1\u7406\u5668\u3002\u3002\u3002\uFF09</li><li>\u52A0\u8F7D\u64CD\u4F5C\u7CFB\u7EDF\u5230\u5185\u5B58\u4E2D\u7684\u7CFB\u7EDF\u533A\uFF0C\u5C5E\u4E8ERAM</li></ul><h2 id="_1-7-\u865A\u62DF\u673A" tabindex="-1"><a class="header-anchor" href="#_1-7-\u865A\u62DF\u673A" aria-hidden="true">#</a> 1.7 \u865A\u62DF\u673A</h2><p>\u4E24\u79CD\u865A\u62DF\u5316\u65B9\u6CD5</p><p>1.\u7B2C\u4E00\u7C7B\u865A\u62DF\u673A</p><p>\u865A\u62DF\u673A\u4F5C\u4E3A\u7528\u6237\u6001\u7684\u8FDB\u7A0B\uFF0C\u5F53\u6267\u884C\u5185\u6838\u6001\u6307\u4EE4\u65F6\uFF0C\u9677\u5165\u865A\u62DF\u673A\u7BA1\u7406\u7A0B\u5E8F\u3002\u865A\u62DF\u673A\u7BA1\u7406\u7A0B\u5E8F\u68C0\u67E5\u8BE5\u6307\u4EE4\u662F\u5426\u662F\u865A\u62DF\u673A\u7684\u64CD\u4F5C\u7CFB\u7EDF\u6267\u884C\u7684\uFF0C\u662F\u5219\u6B63\u786E\u6267\u884C</p><p>2.\u7B2C\u4E8C\u7C7B\u865A\u62DF\u673A</p><p>\u865A\u62DF\u673A\u7684\u64CD\u4F5C\u7CFB\u7EDF\u79F0\u4E3A\u5BA2\u6237\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u5B89\u88C5\u865A\u62DF\u673A\u7684\u5E95\u5C42\u786C\u4EF6\u4E0A\u7684\u64CD\u4F5C\u7CFB\u7EDF\u662F\u5BBF\u4E3B\u64CD\u4F5C\u7CFB\u7EDF</p><p>\u865A\u62DF\u673A\u7BA1\u7406\u7A0B\u5E8F\u4F2A\u88C5\u6210\u5B8C\u6574\u7684\u8BA1\u7B97\u673A\uFF0C\u6709\u865A\u62DF\u8BBE\u5907\uFF0C\u865A\u62DF\u78C1\u76D8</p>',60),p=[h];function d(t,n){return a(),l("div",null,p)}var c=i(r,[["render",d],["__file","01.html.vue"]]);export{c as default};
