import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as d,b as e}from"./app.b2b04f28.js";const r={},i=e('<h2 id="\u6307\u4EE4\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u6307\u4EE4\u5BFB\u5740" aria-hidden="true">#</a> \u6307\u4EE4\u5BFB\u5740</h2><ul><li>\u786E\u5B9A\u4E0B\u4E00\u6761\u8981\u6267\u884C\u7684\u6307\u4EE4\u7684\u5B58\u653E\u5730\u5740</li><li>\u7531\u7A0B\u5E8F\u8BA1\u6570\u5668PC\u6307\u660E</li></ul><h3 id="\u987A\u5E8F\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u987A\u5E8F\u5BFB\u5740" aria-hidden="true">#</a> \u987A\u5E8F\u5BFB\u5740</h3><p>(PC)+&quot;1&quot;-&gt;PC, \u6B64\u5904\u7684&quot;1&quot;\u7406\u89E3\u4E3A1\u4E2A\u6307\u4EE4\u5B57\u957F, \u6BCF\u6B21\u53D6\u6307\u4EE4\u7ED3\u675F\u540E, \u4E00\u5B9A\u4F1APC+&quot;1&quot;</p><blockquote><p>\u7CFB\u7EDF\u91C7\u7528\u53D8\u957F\u6307\u4EE4\u5B57\u7ED3\u6784, \u6307\u4EE4\u5B57\u957F=\u5B58\u50A8\u5B57\u957F=16bit=2B, \u4E3B\u5B58\u6309\u7167\u5B57\u8282\u7F16\u5740 <br> \u8BFB\u5165\u4E00\u4E2A\u5B57, \u6839\u636E\u64CD\u4F5C\u7801\u5224\u65AD\u8FD9\u6761\u6307\u4EE4\u7684\u603B\u5B57\u8282\u6570n, \u4FEE\u6539PC\u7684\u503C, (PC)+n-&gt;PC <br> \u6839\u636E\u6307\u4EE4\u7684\u7C7B\u578B, CPU\u53EF\u80FD\u8FD8\u8981\u8FDB\u884C\u591A\u6B21\u8BBF\u5B58, \u6BCF\u6B21\u8BFB\u5165\u4E00\u4E2A\u5B57</p></blockquote><h3 id="\u8DF3\u8DC3\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u8DF3\u8DC3\u5BFB\u5740" aria-hidden="true">#</a> \u8DF3\u8DC3\u5BFB\u5740</h3><p>\u6267\u884C\u8F6C\u79FB\u7C7B\u6307\u4EE4\u5BFC\u81F4PC\u7684\u503C\u6539\u53D8, \u7C7B\u4F3C\u4E8E\u65E0\u6761\u4EF6\u8F6C\u79FB\u6307\u4EE4JMP\u7B49</p><h2 id="\u6570\u636E\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u5BFB\u5740" aria-hidden="true">#</a> \u6570\u636E\u5BFB\u5740</h2><p>\u5F62\u5F0F\u5730\u5740, \u7B80\u79F0\u4E3AA; \u771F\u5B9E\u5730\u5740, \u7B80\u79F0\u4E3AEA</p><blockquote><p>\u5047\u8BBE\u6307\u4EE4\u5B57\u957F=\u673A\u5668\u5B57\u957F=\u5B58\u50A8\u5B57\u957F, \u64CD\u4F5C\u6570\u4E3A3</p></blockquote><h3 id="\u76F4\u63A5\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u76F4\u63A5\u5BFB\u5740" aria-hidden="true">#</a> \u76F4\u63A5\u5BFB\u5740</h3><p>\u6307\u4EE4\u5B57\u4E2D\u7684\u5F62\u5F0F\u5730\u5740A\u5C31\u662F\u64CD\u4F5C\u6570\u7684\u771F\u5B9E\u5730\u5740EA, \u5373EA=A</p><p>\u4E00\u6B21\u6307\u4EE4\u7684\u6267\u884C:</p><ol><li>\u53D6\u6307\u4EE4 \u8BBF\u5B581\u6B21</li><li>\u6267\u884C\u6307\u4EE4 \u8BBF\u5B581\u6B21 <br> \u6682\u4E0D\u8003\u8651\u5B58\u7ED3\u679C, \u5171\u8BBF\u5B582\u6B21</li></ol><p>\u4F18\u70B9: \u7B80\u5355, \u6307\u4EE4\u6267\u884C\u9636\u6BB5\u4EC5\u8BBF\u95EE\u4E00\u6B21\u4E3B\u5B58, \u4E0D\u9700\u8981\u4E13\u95E8\u8BA1\u7B97\u64CD\u4F5C\u6570\u7684\u5730\u5740</p><h3 id="\u95F4\u63A5\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u95F4\u63A5\u5BFB\u5740" aria-hidden="true">#</a> \u95F4\u63A5\u5BFB\u5740</h3><p>\u6307\u4EE4\u7684\u5730\u5740\u5B57\u6BB5\u7ED9\u51FA\u7684\u5F62\u5F0F\u5730\u5740\u4E0D\u662F\u64CD\u4F5C\u6570\u771F\u6B63\u7684\u5730\u5740, \u800C\u662F\u64CD\u4F5C\u6570\u6709\u6548\u5730\u5740\u6240\u5728\u7684\u5B58\u50A8\u5355\u5143\u7684\u5730\u5740, \u4E5F\u5C31\u662F\u64CD\u4F5C\u6570\u5730\u5740\u7684\u5730\u5740. \u6839\u636E\u95F4\u63A5\u5BFB\u5740\u7684\u6B21\u6570\u53EF\u4EE5\u5206\u4E3A\u4E00\u6B21\u95F4\u63A5\u5BFB\u5740\u548C\u4E24\u6B21\u95F4\u63A5\u5BFB\u5740</p><p>\u4F18\u70B9:</p><ul><li>\u53EF\u6269\u5927\u5BFB\u5740\u8303\u56F4(\u6709\u6548\u5BFB\u5740EA\u7684\u4F4D\u6570\u5927\u4E8E\u5F62\u5F0F\u5730\u5740A\u7684\u4F4D\u6570)</li><li>\u4FBF\u4E8E\u7F16\u5236\u7A0B\u5E8F(\u7528\u95F4\u63A5\u5BFB\u5740\u53EF\u4EE5\u65B9\u4FBF\u5730\u5B8C\u6210\u5B50\u7A0B\u5E8F\u5730\u8FD4\u56DE)</li></ul><h3 id="\u5BC4\u5B58\u5668\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u5BC4\u5B58\u5668\u5BFB\u5740" aria-hidden="true">#</a> \u5BC4\u5B58\u5668\u5BFB\u5740</h3><p>\u5728\u6307\u4EE4\u5B57\u4E2D\u76F4\u63A5\u7ED9\u51FA\u64CD\u4F5C\u6570\u6240\u5728\u5730\u5BC4\u5B58\u5668\u7F16\u53F7, \u5373EA=Ri, \u5176\u64CD\u4F5C\u6570\u5728\u7531Ri\u6240\u6307\u5730\u5BC4\u5B58\u5668\u5185.</p><p>\u4E00\u6B21\u6307\u4EE4\u7684\u6267\u884C:</p><ol><li>\u53D6\u6307\u4EE4 \u8BBF\u5B581\u6B21</li><li>\u6267\u884C\u6307\u4EE4 \u8BBF\u5B580\u6B21 <br> \u6682\u4E0D\u8003\u8651\u5B58\u7ED3\u679C, \u5171\u8BBF\u5B581\u6B21</li></ol><p>\u4F18\u70B9:</p><ul><li>\u6307\u4EE4\u5728\u6267\u884C\u9636\u6BB5\u4E0D\u8BBF\u95EE\u4E3B\u5B58, \u53EA\u8BBF\u95EE\u5BC4\u5B58\u5668</li><li>\u6307\u4EE4\u5B57\u77ED\u4E14\u6267\u884C\u901F\u5EA6\u5FEB, \u652F\u6301\u5411\u91CF/\u77E9\u9635\u8FD0\u7B97</li></ul><p>\u7F3A\u70B9: \u5BC4\u5B58\u5668\u4EF7\u683C\u6602\u8D35, \u8BA1\u7B97\u673A\u4E2D\u5BC4\u5B58\u5668\u7684\u4E2A\u6570\u6709\u9650</p><h3 id="\u5BC4\u5B58\u5668\u7B80\u4ECB\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u5BC4\u5B58\u5668\u7B80\u4ECB\u5BFB\u5740" aria-hidden="true">#</a> \u5BC4\u5B58\u5668\u7B80\u4ECB\u5BFB\u5740</h3><p>\u5BC4\u5B58\u5668Ri\u4E2D\u7ED9\u51FA\u7684\u4E0D\u662F\u4E00\u4E2A\u64CD\u4F5C\u6570, \u800C\u662F\u64CD\u4F5C\u6570\u6240\u5728\u4E3B\u5B58\u5355\u5143\u7684\u5730\u5740, \u5373EA=(Ri)</p><p>\u4E00\u6B21\u6307\u4EE4\u7684\u6267\u884C:</p><ol><li>\u53D6\u6307\u4EE4 \u8BBF\u5B581\u6B21</li><li>\u6267\u884C\u6307\u4EE4 \u8BBF\u5B581\u6B21 <br> \u6682\u4E0D\u8003\u8651\u5B58\u7ED3\u679C, \u5171\u8BBF\u5B582\u6B21</li></ol><p>\u7279\u70B9: \u4E0E\u4E00\u822C\u95F4\u63A5\u5BFB\u5740\u76F8\u6BD4\u901F\u5EA6\u66F4\u5FEB, \u4F46\u6307\u4EE4\u7684\u6267\u884C\u9636\u6BB5\u9700\u8981\u8BBF\u95EE\u4E3B\u5B58(\u56E0\u4E3A\u64CD\u4F5C\u6570\u5728\u4E3B\u5B58\u4E2D)</p><h3 id="\u9690\u542B\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u9690\u542B\u5BFB\u5740" aria-hidden="true">#</a> \u9690\u542B\u5BFB\u5740</h3><p>\u9690\u542B\u5BFB\u5740: \u4E0D\u662F\u660E\u663E\u5730\u7ED9\u51FA\u64CD\u4F5C\u6570\u7684\u5730\u5740, \u800C\u662F\u5728\u6307\u4EE4\u4E2D\u9690\u542B\u7740\u64CD\u4F5C\u6570\u7684\u5730\u5740</p><p>\u4F18\u70B9: \u6709\u5229\u4E8E\u7F29\u77ED\u6307\u4EE4\u5B57\u957F</p><p>\u7F3A\u70B9: \u9700\u8981\u589E\u52A0\u5B58\u50A8\u64CD\u4F5C\u6570\u6216\u8005\u9690\u542B\u5730\u5740\u7684\u786C\u4EF6</p><h3 id="\u7ACB\u5373\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u7ACB\u5373\u5BFB\u5740" aria-hidden="true">#</a> \u7ACB\u5373\u5BFB\u5740</h3><p>\u5F62\u5F0F\u5730\u5740A\u5C31\u662F\u64CD\u4F5C\u6570\u672C\u8EAB, \u53C8\u79F0\u4E3A\u7ACB\u5373\u6570, \u4E00\u822C\u91C7\u7528\u8865\u7801\u5F62\u5F0F. #\u8868\u793A\u7ACB\u5373\u5BFB\u5740\u7279\u5F81</p><p>\u4E00\u6761\u6307\u4EE4\u7684\u6267\u884C:</p><ol><li>\u53D6\u6307\u4EE4 \u8BBF\u5B581\u6B21</li><li>\u6267\u884C\u6307\u4EE4 \u8BBF\u5B580\u6B21 <br> \u6682\u4E0D\u8003\u8651\u5B58\u7ED3\u679C, \u5171\u8BBF\u5B581\u6B21</li></ol><p>\u4F18\u70B9: \u6307\u4EE4\u6267\u884C\u9636\u6BB5\u4E0D\u8BBF\u95EE\u4E3B\u5B58, \u6307\u4EE4\u6267\u884C\u65F6\u95F4\u6700\u77ED</p><p>\u7F3A\u70B9: A\u7684\u4F4D\u6570\u9650\u5236\u4E86\u7ACB\u5373\u6570\u7684\u8303\u56F4</p><h3 id="\u504F\u79FB\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u504F\u79FB\u5BFB\u5740" aria-hidden="true">#</a> \u504F\u79FB\u5BFB\u5740</h3><h4 id="\u57FA\u5740\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u57FA\u5740\u5BFB\u5740" aria-hidden="true">#</a> \u57FA\u5740\u5BFB\u5740</h4><p>\u5C06CPU\u4E2D\u57FA\u5740\u5BC4\u5B58\u5668(BR)\u4E2D\u7684\u5185\u5BB9\u52A0\u4E0A\u6307\u4EE4\u683C\u5F0F\u4E2D\u7684\u5F62\u5F0F\u5730\u5740A, \u800C\u5F62\u6210\u64CD\u4F5C\u6570\u7684\u6709\u6548\u5730\u5740, \u5373EA=(BR)+A</p><p>\u6709\u4E24\u79CD\u65B9\u6CD5:</p><ol><li>\u91C7\u7528\u4E13\u7528\u5BC4\u5B58\u5668BR\u4F5C\u4E3A\u57FA\u5740\u5BC4\u5B58\u5668</li><li>\u91C7\u7528\u901A\u7528\u5BC4\u5B58\u5668\u4F5C\u4E3A\u57FA\u5740\u5BC4\u5B58\u5668, \u5728\u6307\u4EE4\u4E2D\u9700\u8981\u6307\u660E, \u8981\u5C06\u54EA\u4E2A\u901A\u7528\u5BC4\u5B58\u5668\u4F5C\u4E3A\u57FA\u5740\u5BC4\u5B58\u5668\u4F7F\u7528, \u4E00\u822C\u7528R0\u8868\u793A</li></ol><p>\u4F18\u70B9: \u4FBF\u4E8E\u7A0B\u5E8F&quot;\u6D6E\u52A8&quot;, \u65B9\u4FBF\u5B9E\u73B0\u591A\u9053\u7A0B\u5E8F\u5E76\u53D1\u8FD0\u884C</p><p>\u62D3\u5C55: \u7A0B\u5E8F\u8FD0\u884C\u524D, CPU\u5C06BR\u7684\u503C\u4FEE\u6539\u4E3A\u8BE5\u7A0B\u5E8F\u7684\u8D77\u59CB\u5730\u5740(\u5B58\u5728\u64CD\u4F5C\u7CFB\u7EDFPCB\u4E2D)</p><p>\u6CE8:</p><ul><li>\u57FA\u5740\u5BC4\u5B58\u5668\u662F\u9762\u5411\u64CD\u4F5C\u7CFB\u7EDF\u7684, \u5176\u5185\u5BB9\u662F\u7531\u64CD\u4F5C\u7CFB\u7EDF\u6216\u8005\u7BA1\u7406\u7A0B\u5E8F\u786E\u5B9A. \u5728\u7A0B\u5E8F\u6267\u884C\u8FC7\u7A0B\u4E2D, \u57FA\u5740\u5BC4\u5B58\u5668\u7684\u5185\u5BB9\u4E0D\u53D8(\u4F5C\u4E3A\u57FA\u5730\u5740), \u5F62\u5F0F\u5730\u5740\u53EF\u53D8(\u4F5C\u4E3A\u504F\u79FB\u91CF)</li><li>\u5F53\u91C7\u7528\u901A\u7528\u5BC4\u5B58\u5668\u4F5C\u4E3A\u57FA\u5740\u5BC4\u5B58\u5668\u65F6, \u53EF\u7531\u7528\u6237\u51B3\u5B9A\u54EA\u4E2A\u5BC4\u5B58\u5668\u4F5C\u4E3A\u57FA\u5740\u5BC4\u5B58\u5668, \u4F46\u5176\u5185\u5BB9\u4ECD\u7531\u64CD\u4F5C\u7CFB\u7EDF\u51B3\u5B9A</li></ul><h4 id="\u53D8\u5740\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u53D8\u5740\u5BFB\u5740" aria-hidden="true">#</a> \u53D8\u5740\u5BFB\u5740</h4><p>\u6709\u6548\u5730\u5740EA\u7B49\u4E8E\u6307\u4EE4\u5B57\u4E2D\u7684\u5F62\u5F0F\u5730\u5740A\u4E0E\u53D8\u5740\u5BC4\u5B58\u5668IX\u7684\u5185\u5BB9\u4E4B\u548C, \u5373EA=(IX)+A, \u5176\u4E2DIX\u53EF\u4E3A\u53D8\u5740\u5BC4\u5B58\u5668(\u4E13\u7528), \u4E5F\u53EF\u7528\u901A\u7528\u5BC4\u5B58\u5668\u4F5C\u4E3A\u53D8\u5740\u5BC4\u5B58\u5668</p><p>\u6CE8: \u53D8\u5740\u5BC4\u5B58\u5668\u662F\u9762\u5411\u7528\u6237\u7684, \u5728\u7A0B\u5E8F\u6267\u884C\u7684\u8FC7\u7A0B\u4E2D, \u53D8\u5740\u5BC4\u5B58\u5668\u7684\u5185\u5BB9\u53EF\u7531\u7528\u6237\u6539\u53D8(IX\u4F5C\u4E3A\u504F\u79FB\u91CF), \u5F62\u5F0F\u5730\u5740A\u4E0D\u53D8(\u4F5C\u4E3A\u57FA\u5730\u5740)</p><p>\u53D8\u5740\u5BFB\u5740\u4F5C\u7528: \u5728\u6570\u7EC4\u5904\u7406\u7684\u8FC7\u7A0B\u4E2D, \u53EF\u8BBE\u5B9AA\u4E3A\u6570\u7EC4\u7684\u9996\u5730\u5740, \u4E0D\u65AD\u6539\u53D8\u53D8\u5740\u5BC4\u5B58\u5668IX\u7684\u5185\u5BB9, \u4FBF\u53EF\u4EE5\u5F88\u5BB9\u6613\u5F62\u6210\u6570\u7EC4\u4E2D\u4EFB\u4E00\u6570\u636E\u7684\u5730\u5740, \u7279\u522B\u9002\u5408\u7F16\u5236\u5FAA\u73AF\u7A0B\u5E8F</p><h4 id="\u57FA\u5740-\u53D8\u5740\u590D\u5408\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u57FA\u5740-\u53D8\u5740\u590D\u5408\u5BFB\u5740" aria-hidden="true">#</a> \u57FA\u5740&amp;\u53D8\u5740\u590D\u5408\u5BFB\u5740</h4><ul><li>\u57FA\u5740\u5BFB\u5740: EA=(BR)+A</li><li>\u53D8\u5740\u5BFB\u5740: EA=(IX)+A</li><li>\u5148\u57FA\u5740\u540E\u53D8\u5740\u5BFB\u5740: EA=(IX)+(BX)+A</li></ul><h4 id="\u76F8\u5BF9\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u76F8\u5BF9\u5BFB\u5740" aria-hidden="true">#</a> \u76F8\u5BF9\u5BFB\u5740</h4><p>\u628A\u7A0B\u5E8F\u8BA1\u6570\u5668PC\u7684\u5185\u5BB9\u52A0\u4E0A\u6307\u4EE4\u683C\u5F0F\u4E2D\u7684\u5F62\u5F0F\u5730\u5740A\u800C\u5F62\u6210\u64CD\u4F5C\u6570\u7684\u6709\u6548\u5730\u5740, \u5373EA=(PC)+A, \u5176\u4E2DA\u662F\u76F8\u5BF9\u4E8EPC\u6240\u6307\u5730\u5740\u7684\u4F4D\u79FB\u91CF, \u53EF\u6B63\u53EF\u8D1F, \u8865\u7801\u8868\u793A</p><p>\u6CE8: \u738B\u9053\u4E66\u4E0A\u7684\u5C0F\u9519\u8BEF -- &quot;A\u662F\u76F8\u5F53\u4E8E\u5F53\u524D\u6307\u4EE4\u5730\u5740\u7684\u4F4D\u79FB\u91CF&quot;\u8FD9\u53E5\u8BDD\u662F\u9519\u8BEF\u7684, \u56E0\u4E3A\u53D6\u51FA\u5F53\u524D\u6307\u4EE4\u540E, PC+&quot;1&quot;\u6307\u5411\u7684\u662F\u4E0B\u4E00\u6761\u6307\u4EE4</p><p>\u4F18\u70B9: \u64CD\u4F5C\u6570\u7684\u5730\u5740\u4E0D\u662F\u56FA\u5B9A\u7684, \u5B83\u968F\u7740PC\u7684\u503C\u7684\u53D8\u5316\u800C\u53D8\u5316, \u5E76\u4E14\u4E0E\u6307\u4EE4\u5730\u5740\u4E4B\u95F4\u603B\u662F\u76F8\u5DEE\u4E00\u4E2A\u56FA\u5B9A\u7684\u503C, \u56E0\u6B64\u4FBF\u4E8E\u7A0B\u5E8F\u6D6E\u52A8(\u4E00\u6BB5\u4EE3\u7801\u5728\u7A0B\u5E8F\u5185\u90E8\u7684\u6D6E\u52A8), \u76F8\u5BF9\u5BFB\u5740\u5E7F\u6CDB\u7528\u4E8E\u8F6C\u79FB\u6307\u4EE4</p><h3 id="\u5806\u6808\u5BFB\u5740" tabindex="-1"><a class="header-anchor" href="#\u5806\u6808\u5BFB\u5740" aria-hidden="true">#</a> \u5806\u6808\u5BFB\u5740</h3><p>\u64CD\u4F5C\u6570\u653E\u5728\u5806\u6808\u4E2D, \u9690\u542B\u4F7F\u7528\u5806\u6808\u6307\u9488(SP)\u4F5C\u4E3A\u64CD\u4F5C\u6570\u5730\u5740</p><p>\u5806\u6808\u662F\u5B58\u50A8\u5668(\u6216\u4E13\u7528\u5BC4\u5B58\u5668\u7EC4)\u4E2D\u4E00\u5757\u7279\u5B9A\u7684\u6309&quot;\u540E\u8FDB\u5148\u51FA(LIFO)&quot;\u539F\u5219\u7BA1\u7406\u7684\u5B58\u50A8\u533A, \u8BE5\u5B58\u50A8\u533A\u4E2D\u88AB\u8BFB/\u5199\u5355\u5143\u7684\u5730\u5740\u662F\u7528\u4E00\u4E2A\u7279\u5B9A\u7684\u5BC4\u5B58\u5668\u7ED9\u51FA\u7684, \u8BE5\u5BC4\u5B58\u5668\u79F0\u4E3A\u5806\u6808\u6307\u9488(SP)</p><h2 id="cisc-risc" tabindex="-1"><a class="header-anchor" href="#cisc-risc" aria-hidden="true">#</a> CISC&amp;RISC</h2><h3 id="cisc\u548Crisc" tabindex="-1"><a class="header-anchor" href="#cisc\u548Crisc" aria-hidden="true">#</a> CISC\u548CRISC</h3><p>80-20\u89C4\u5F8B: \u5178\u578B\u7A0B\u5E8F\u4E2D80%\u7684\u8BED\u53E5\u4EC5\u4EC5\u4F7F\u7528\u5904\u7406\u673A\u4E2D20%\u7684\u6307\u4EE4</p><h4 id="cisc" tabindex="-1"><a class="header-anchor" href="#cisc" aria-hidden="true">#</a> CISC</h4><p>CISC: Complex Instruction Set Computer</p><p>\u8BBE\u8BA1\u601D\u8DEF: \u4E00\u6761\u6307\u4EE4\u5B8C\u6210\u4E00\u4E2A\u590D\u6742\u7684\u57FA\u672C\u529F\u80FD</p><p>\u4EE3\u8868: X86\u67B6\u6784, \u4E3B\u8981\u7528\u4E8E\u7B14\u8BB0\u672C, \u53F0\u5F0F\u673A\u7B49</p><blockquote><p>\u6BD4\u5982\u8BBE\u8BA1\u4E00\u5957\u80FD\u5B9E\u73B0\u6574\u6570, \u77E9\u9635\u52A0/\u51CF/\u4E58\u8FD0\u7B97\u7684\u6307\u4EE4\u96C6 <br> CISC\u7684\u8BBE\u8BA1\u601D\u8DEF: \u9664\u4E86\u63D0\u4F9B\u6574\u6570\u7684\u52A0\u51CF\u4E58\u6307\u4EE4\u9664\u4E4B\u5916, \u8FD8\u63D0\u4F9B\u77E9\u9635\u7684\u52A0\u6CD5\u6307\u4EE4, \u77E9\u9635\u7684\u51CF\u6CD5\u6307\u4EE4, \u77E9\u9635\u7684\u4E58\u6CD5\u6307\u4EE4. <br> \u4E00\u6761\u6307\u4EE4\u53EF\u4EE5\u7531\u4E00\u4E2A\u4E13\u95E8\u7684\u7535\u8DEF\u5B8C\u6210 <br> \u6709\u7684\u590D\u6742\u6307\u4EE4\u7528\u7EAF\u786C\u4EF6\u5B9E\u73B0\u5F88\u56F0\u96BE, \u91C7\u7528&quot;\u5B58\u50A8\u7A0B\u5E8F&quot;\u7684\u8BBE\u8BA1\u601D\u60F3, \u7531\u4E00\u4E2A\u6BD4\u8F83\u901A\u7528\u7684\u7535\u8DEF\u914D\u5408\u5B58\u50A8\u90E8\u4EF6\u5B8C\u6210\u4E00\u6761\u6307\u4EE4</p></blockquote><h4 id="risc" tabindex="-1"><a class="header-anchor" href="#risc" aria-hidden="true">#</a> RISC</h4><p>\u8BBE\u8BA1\u601D\u8DEF: \u4E00\u6761\u6307\u4EE4\u5B8C\u6210\u4E00\u4E2A\u57FA\u672C&quot;\u52A8\u4F5C&quot;; \u591A\u6761\u6307\u4EE4\u7EC4\u5408\u5B8C\u6210\u4E00\u4E2A\u590D\u6742\u7684\u57FA\u672C\u529F\u80FD</p><p>\u4EE3\u8868: ARM\u67B6\u6784, \u4E3B\u8981\u7528\u4E8E\u624B\u673A, \u5E73\u677F\u7B49</p><blockquote><p>\u6BD4\u5982\u8BBE\u8BA1\u4E00\u5957\u80FD\u5B9E\u73B0\u6574\u6570, \u77E9\u9635\u52A0/\u51CF/\u4E58\u8FD0\u7B97\u7684\u6307\u4EE4\u96C6 <br> RISC\u7684\u601D\u8DEF: \u53EA\u63D0\u4F9B\u6574\u6570\u7684\u52A0\u51CF\u4E58\u6307\u4EE4 <br> \u4E00\u6761\u6307\u4EE4\u4E00\u4E2A\u7535\u8DEF, \u7535\u8DEF\u8BBE\u8BA1\u76F8\u5BF9\u7B80\u5355, \u529F\u8017\u66F4\u4F4E</p></blockquote><h3 id="\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#\u533A\u522B" aria-hidden="true">#</a> \u533A\u522B</h3><table><thead><tr><th></th><th>CISC</th><th>RISC</th></tr></thead><tbody><tr><td>\u6307\u4EE4\u7CFB\u7EDF</td><td>\u590D\u6742, \u5E9E\u5927</td><td>\u7B80\u5355, \u7CBE\u7B80</td></tr><tr><td>\u6307\u4EE4\u6570\u76EE</td><td>\u4E00\u822C\u5927\u4E8E200\u6761</td><td>\u4E00\u822C\u5C0F\u4E8E100\u6761</td></tr><tr><td>\u6307\u4EE4\u5B57\u957F</td><td>\u4E0D\u56FA\u5B9A</td><td>\u5B9A\u957F</td></tr><tr><td>\u53EF\u8BBF\u5B58\u6307\u4EE4</td><td>\u4E0D\u52A0\u9650\u5236</td><td>\u53EA\u6709Load/Store\u6307\u4EE4</td></tr><tr><td>\u5404\u79CD\u6307\u4EE4\u6267\u884C\u65F6\u95F4</td><td>\u76F8\u5DEE\u8F83\u5927</td><td>\u7EDD\u5927\u591A\u6570\u5728\u4E00\u4E2A\u5468\u671F\u5185\u5B8C\u6210</td></tr><tr><td>\u5404\u79CD\u6307\u4EE4\u7684\u4F7F\u7528\u9891\u5EA6</td><td>\u76F8\u5DEE\u5F88\u5927</td><td>\u90FD\u6BD4\u8F83\u5E38\u7528</td></tr><tr><td>\u901A\u7528\u5BC4\u5B58\u5668\u6570\u91CF</td><td>\u8F83\u5C11</td><td>\u591A</td></tr><tr><td>\u76EE\u6807\u4EE3\u7801</td><td>\u96BE\u4EE5\u7528\u4F18\u5316\u7F16\u8BD1\u751F\u6210\u641E\u7B11\u7684\u76EE\u6807\u4EE3\u7801\u7A0B\u5E8F</td><td>\u91C7\u7528\u4F18\u5316\u7684\u7F16\u8BD1\u7A0B\u5E8F, \u751F\u6210\u4EE3\u7801\u8F83\u4E3A\u9AD8\u6548</td></tr><tr><td>\u63A7\u5236\u65B9\u5F0F</td><td>\u7EDD\u5927\u591A\u6570\u4E3A\u5FAE\u7A0B\u5E8F\u63A7\u5236</td><td>\u7EDD\u5927\u591A\u6570\u4E3A\u7EC4\u5408\u903B\u8F91\u63A7\u5236</td></tr><tr><td>\u6307\u4EE4\u6D41\u6C34\u7EBF</td><td>\u53EF\u4EE5\u901A\u8FC7\u4E00\u5B9A\u65B9\u5F0F\u5B9E\u73B0</td><td>\u5FC5\u987B\u5B9E\u73B0</td></tr></tbody></table>',77),h=[i];function l(p,o){return a(),d("div",null,h)}var s=t(r,[["render",l],["__file","04.html.vue"]]);export{s as default};
